declare class DbContext {
    constructor(connection: DbConnection);
    selectAsync<T extends BusinessObject>(entity: string, filter: T): Promise<T[]>;
    deleteAsync<T extends BusinessObject>(entity: string, filter: T);
    insertAsync<T extends BusinessObject>(entity: string, obj: T): Promise<number>;
    updateAsync<T extends BusinessObject>(entity: string, obj: T): Promise<number>;
    saveAsync<T extends BusinessObject>(entity: string, obj: T): Promise<number>;

    execProcedureAsync(procedureName: string, argsArray: any[]): Promise<any>;
    execSqlAsync(sql: string): Promise<any>;
}

declare interface DbConnection {
    user: string,
    password: string,
    database: string,
    host: string,
    port: number
}

declare interface BusinessObject {
    id: number
}
